$(document).ready(function(){$(".delete-group").bind("ajax:complete",function(){$(this).closest(".group-list-item").remove()}),$(".launch-add-to-group-modal").click(function(){$(".reference-id").each(function(){this.checked&&$(".group_id").append("<input type='text' name='reference_ids[]' value='"+this.value+"'/>")})}),$("#addToGroup").bind("ajax:complete",function(e,t,n){$(".message-container").html(t.responseText)}),$(".js-group-toggle").click(function(){$(this).parent().next().slideToggle()})});