$(document).ready(function(){function t(t,e){$.ajax({type:"patch",dataType:"JSON",url:"/notes/"+t+"/update_status",data:{status:e}})}$(".note-single__delete").bind("ajax:complete",function(){$(this).closest(".note-container").remove()}),$(".note-single__accolade").click(function(){event.preventDefault();var e=$(this),a=e.attr("note-id"),n=$("#noteDown"+a);e.hasClass("active-thumb")?(e.removeClass("active-thumb"),t(a,null)):(e.addClass("active-thumb"),n.removeClass("active-thumb"),t(a,"Positive"))}),$(".note-single__opportunity").click(function(){event.preventDefault();var e=$(this),a=e.attr("note-id"),n=$("#noteUp"+a);e.hasClass("active-thumb")?(e.removeClass("active-thumb"),t(a,null)):(e.addClass("active-thumb"),n.removeClass("active-thumb"),t(a,"Negative"))})});