$(document).ready(function(){$("#new_report").on("ajax:success",function(e,r,o){$("#report_name").val(""),$("#report_type").val(""),$("#report_segment_id").val(""),$(".form-message-container").html("<div class='alert notice'>Your report is ready! <strong><a target='_blank' href='/reports/"+r.id+"/open_report.pdf' style='color: #ffffff'>Download now \xbb</a></strong></div>")}).on("ajax:error",function(e,r,o){$(".form-message-container").html(r.responseText)}),$("#emailReport").bind("ajax:complete",function(e,r,o){$(".message-container").html(r.responseText)}),$(".delete-report").bind("ajax:complete",function(){$(this).closest(".report-container").remove()}),$(".launch-send-report-modal").click(function(){var e=$(this).attr("report-id");$(".send-report-report-id-hidden").val(e)})});