$(document).ready(function(){$("#new_invite").on("ajax:success",function(e,s,a){$("input:radio").attr("checked",!1),$("input:text").val(""),$(".invite-message-container").html("Invite Sent Successfully!")}).on("ajax:error",function(e,s,a){var r=JSON.parse(s.responseText).errors;$(".invite-message-container").html(r)}),$("#add_shadowers_to_experience").on("ajax:success",function(e,s,a){$(".add-shadower-message-container").html("Shadowers Added Successfully!")}).on("ajax:error",function(e,s,a){var r=JSON.parse(s.responseText).errors;$(".add-shadower-message-container").html(r)}),$(".js-existing-shadower-invite").on("click",function(){var e=$(this);e.hasClass("active-row")?(e.find(".user-id-ux-field").val(""),e.removeClass("active-row")):(e.addClass("active-row"),e.find(".user-id-ux-field").val(e.attr("user-id")))}),$(".remove-shadower").bind("ajax:complete",function(){$(this).closest(".shadower-row").remove()})});